mapper.select:
select e.*,d.name deptName from emp e left join dept d on d.id = e.dept_id


        e.name like concat('%',#{name},'%')


        and e.gender=#{gender}


        and entry_date between #{begin} and #{end}

  order by e.update_time desc

select
if(gender = 1,'男性员工','女性员工') name,
count(*) value
from emp group by gender;

mapper.update:
UPDATE emp
username = #{username}, name = #{name}, password = #{password}, gender = #{gender}, phone = #{phone}, job = #{job}, salary = #{salary}, image = #{image}, entry_date = #{entryDate}, dept_id = #{deptId}, update_time = #{updateTime}
WHERE id = #{id}
