mapper.select:
select s.*,c.name clazzName from student s left join clazz c on s.clazz_id = c.id


        s.name like concat('%',#{name},'%')


        and  clazz_id = #{clazzId}


        and  degree = #{degree}


order by update_time desc

mapper.update:
update student


     id_card=#{idCard}


     is_colledge=#{isCollege}


       address=#{address}


        degree=#{degree}


where id=#{id}
